import React, { useState, useEffect } from "react";
import "./App.css"; // Import the CSS file for styling

function App() {
  const [text, setText] = useState("");
  const [predictedText, setPredictedText] = useState("");
  const [backendStatus, setBackendStatus] = useState("Connecting...");

  useEffect(() => {
    // Check backend connection status
    fetch("/predict")
      .then(() => setBackendStatus("Connected"))
      .catch(() => setBackendStatus("Disconnected"));
  }, []);

  const handleInputChange = (event) => {
    const newText = event.target.value;
    setText(newText);

    // Update predicted text without making a request to the backend
    const predictedText = newText + " world";
    setPredictedText(predictedText);
  };

  return (
    <div className="container">
      <h1 className="title">Predictive Text App</h1>
      <div className="status">
        <strong>Backend Status:</strong> {backendStatus}
      </div>
      <textarea
        className="input"
        value={text}
        onChange={handleInputChange}
        rows="5"
        cols="50"
      ></textarea>
      <br />
      <div className="predicted-text">
        <h2>Predicted Text:</h2>
        <p>{predictedText}</p>
      </div>
    </div>
  );
}

export default App;
